    List<List<Integer>> res =new ArrayList<>();
    List<List<Integer>> path =new ArrayList<>();
//    visit只是用来剪枝,不能设为false,因为如果这个走过了还没有结束,证明这个是无效的路径,因为只能向下和向右走
    boolean[][] visit=null;
    public List<List<Integer>> pathWithObstacles(int[][] obstacleGrid) {
        visit=new boolean[obstacleGrid.length][obstacleGrid[0].length];
        dfs(obstacleGrid,0,0);
        return res;
    }
    void dfs(int[][] obstacleGrid,int i,int j){
        if (!res.isEmpty())return;
        if (i>obstacleGrid.length-1||j>obstacleGrid[0].length-1||obstacleGrid[i][j]==1)return;
        if (visit[i][j])return;
        if (i==obstacleGrid.length-1&&j==obstacleGrid[i].length-1){
            List<Integer> list = Arrays.asList(i, j);
            path.add(list);
            res=new ArrayList<>(path);
        }
        visit[i][j]=true;
        List<Integer> list = Arrays.asList(i, j);
        path.add(list);
        dfs(obstacleGrid, i+1, j);
        dfs(obstacleGrid, i, j+1);
        path.remove(path.size()-1);
    }
